import{_ as t}from"./plugin-vue_export-helper-c27b6911.js";import{o as e,c as d,a as l}from"./app-90f462c1.js";const a={},r=l('<h1 id="常用快捷键" tabindex="-1"><a class="header-anchor" href="#常用快捷键" aria-hidden="true">#</a> 常用快捷键</h1><p>熟练使用快捷键可以提高我们的开发效率，那如何记住快捷键呢？</p><ul><li>快捷键有很多，没必要都记住，只需记一些常用的快捷键。</li><li>记住快捷键的一个有效方法就是平时多使用它。</li><li>对于一些相似的操作，它们的快捷键也是相似的。如行注释快捷键是<code>Ctrl</code> + <code>/</code>，块注释的快捷键跟行注释相比只是多了一个<code>Shift</code>，即先按<code>Shift</code>，再按<code>Ctrl</code> + <code>/</code>，所以记住了<code>Ctrl</code> + <code>/</code><br> ，就可以容易地记住<code>Shift</code> +<code>Ctrl</code> + <code>/</code>。</li></ul><h2 id="_1-基本操作" tabindex="-1"><a class="header-anchor" href="#_1-基本操作" aria-hidden="true">#</a> 1 基本操作</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Ctrl+D</code>（原<code>Shift+Alt+DownArrow</code>）</td><td style="text-align:left;">向下复制一行</td></tr><tr><td style="text-align:left;"><code>Ctrl+Y</code>（原<code>Ctrl+Shift+K</code>）</td><td style="text-align:left;">删除一行</td></tr><tr><td style="text-align:left;"><code>Alt+F1</code>（原无）</td><td style="text-align:left;">在资源管理器中显示当前活动文件</td></tr></tbody></table><h2 id="_2-查找、替换操作" tabindex="-1"><a class="header-anchor" href="#_2-查找、替换操作" aria-hidden="true">#</a> 2 查找、替换操作</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Ctrl</code> + <code>F</code></td><td style="text-align:left;">在当前文件进行文本查找</td></tr><tr><td style="text-align:left;"><code>Shift</code> +<code>Ctrl</code> + <code>F</code></td><td style="text-align:left;">根据输入内容查找整个项目 或 指定目录内文件</td></tr><tr><td style="text-align:left;"><code>Shift</code> +<code>Alt</code> + <code>F</code></td><td style="text-align:left;">选择一个目录，在该目录中全局查找</td></tr><tr><td style="text-align:left;"><code>Ctrl</code> + <code>P</code></td><td style="text-align:left;">按文件名查找文件</td></tr></tbody></table><h2 id="_3-注释" tabindex="-1"><a class="header-anchor" href="#_3-注释" aria-hidden="true">#</a> 3 注释</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Ctrl</code> + <code>/</code></td><td style="text-align:left;">行注释</td></tr><tr><td style="text-align:left;"><code>Shift</code> +<code>Ctrl</code> + <code>/</code>（原<code>Shift+Alt+A</code>）</td><td style="text-align:left;">块注释 输入<code>/**</code>，然后按回车</td></tr></tbody></table><h2 id="_4-上下移动一行" tabindex="-1"><a class="header-anchor" href="#_4-上下移动一行" aria-hidden="true">#</a> 4 上下移动一行</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Alt+UpArrow/DownArrow</code></td><td style="text-align:left;">上下移动一行</td></tr></tbody></table><h2 id="_5-多光标操作" tabindex="-1"><a class="header-anchor" href="#_5-多光标操作" aria-hidden="true">#</a> 5 多光标操作</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Alt</code></td><td style="text-align:left;">多光标操作</td></tr><tr><td style="text-align:left;"><code>Shift+Alt</code></td><td style="text-align:left;">多光标操作，垂直方向移动光标，在垂直的一列中插入光标</td></tr></tbody></table><h2 id="_6-缩进代码块" tabindex="-1"><a class="header-anchor" href="#_6-缩进代码块" aria-hidden="true">#</a> 6 缩进代码块</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Tab</code></td><td style="text-align:left;">选择的代码块（多行代码）整体右移动</td></tr><tr><td style="text-align:left;"><code>Shift+Tab</code></td><td style="text-align:left;">选择的代码块（多行代码）整体左移动</td></tr></tbody></table><h2 id="_7-外观" tabindex="-1"><a class="header-anchor" href="#_7-外观" aria-hidden="true">#</a> 7 外观</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Ctrl+` </code></td><td style="text-align:left;">显示和隐藏终端</td></tr><tr><td style="text-align:left;"><code>Ctrl+B</code></td><td style="text-align:left;">显示和隐藏侧边栏</td></tr></tbody></table><h2 id="其他" tabindex="-1"><a class="header-anchor" href="#其他" aria-hidden="true">#</a> 其他</h2><table><thead><tr><th style="text-align:left;">快捷键</th><th style="text-align:left;">含义</th></tr></thead><tbody><tr><td style="text-align:left;"><code>Ctrl+Enter</code></td><td style="text-align:left;">快速移动光标到下一行</td></tr><tr><td style="text-align:left;"><code>Shift+Ctrl+Enter</code></td><td style="text-align:left;">快速移动光标到上一行</td></tr><tr><td style="text-align:left;"><code>Alt+LeftArrow/RightArrow</code></td><td style="text-align:left;">快速定位到前后历史文件</td></tr></tbody></table>',19),o=[r];function c(h,i){return e(),d("div",null,o)}const f=t(a,[["render",c],["__file","shortcut-key.html.vue"]]);export{f as default};
